package decoder

import (
	"github.com/ethereum/go-ethereum/common/hexutil"
	"github.com/stretchr/testify/require"
	"github.com/tiennampham23/berachain-airdrop/pkg/dapp/beranames"
	"testing"
)

func Test_Decode(t *testing.T) {

	abi := beranames.BeranameGeneratedMetaData.ABI

	data, err := hexutil.Decode("")
	require.NoError(t, err)
	p, err := Decode(abi, data)
	require.NoError(t, err)

	t.Log(p)
}
